# Load or create label mapping
label_mapping_path = PATHS['models'] / 'label_mapping.json'
canonical_mapping_path = PATHS['models'] / 'canonical_mapping.json'

def load_label_mapping(path: Path, model_output_dim: int) -> List[str]:
    """Load label mapping with fallback to generic labels."""
    if path.exists():
        with open(path, 'r') as f:
            labels = json.load(f)
        if len(labels) == model_output_dim:
            return labels
        else:
            print(f"‚ö†Ô∏è  Label count mismatch: {len(labels)} vs {model_output_dim}")

    # Fallback to generic
    print(f"‚ö†Ô∏è  Using generic labels")
    return [f"class_{i}" for i in range(model_output_dim)]

def load_canonical_mapping(path: Path) -> Dict[str, str]:
    """Load canonical mapping (34 classes ‚Üí 6 core drums)."""
    if path.exists():
        with open(path, 'r') as f:
            return json.load(f)
    return {}

# Load mappings
if 'model1' in models_dict:
    model_dim = models_dict['model1'].output_shape[-1]
    LABELS = load_label_mapping(label_mapping_path, model_dim)
    print(f"‚úÖ Loaded {len(LABELS)} labels")
    print(f"   Sample: {LABELS[:6]}")
else:
    LABELS = TARGET_LABELS
    print(f"‚úÖ Using default target labels: {LABELS}")

CANONICAL_MAP = load_canonical_mapping(canonical_mapping_path)
if CANONICAL_MAP:
    print(f"‚úÖ Loaded canonical mapping: {len(CANONICAL_MAP)} entries")
else:
    print(f"‚ö†Ô∏è  No canonical mapping found, using direct labels")

# Classification helper functions
def hash_file(path: Path, algorithm: str = 'md5') -> str:
    """Compute file hash for deduplication."""
    hasher = hashlib.md5() if algorithm == 'md5' else hashlib.sha256()
    with open(path, 'rb') as f:
        for chunk in iter(lambda: f.read(8192), b''):
            hasher.update(chunk)
    return hasher.hexdigest()

def discover_audio_files(root: Path, extensions: List[str] = None) -> List[Path]:
    """Recursively discover audio files."""
    if extensions is None:
        extensions = ['.wav', '.mp3', '.flac', '.aiff', '.aif']

    files = []
    for ext in extensions:
        files.extend(root.rglob(f'*{ext}'))
    return sorted(files)

def batch_tensorize(mfcc_list: List[np.ndarray]) -> np.ndarray:
    """Convert list of MFCC arrays to model input tensor."""
    return np.array(mfcc_list)

def apply_canonical_mapping(label: str, mapping: Dict[str, str]) -> str:
    """Map fine-grained label to canonical class."""
    return mapping.get(label, label)

print("‚úÖ Classification helper functions defined")

# Discover files to classify
print(f"\nDiscovering audio files in: {ARCHIVE_PATH}")
audio_files = discover_audio_files(ARCHIVE_PATH)
print(f"‚úÖ Found {len(audio_files):,} audio files")

if len(audio_files) == 0:
    print("‚ö†Ô∏è  No audio files found. Check ARCHIVE_PATH.")
else:
    print(f"   Sample files:")
    for f in audio_files[:5]:
        print(f"     - {f.relative_to(ARCHIVE_PATH)}")

# Regenerate organized archive
if results_list and COPY_MODE != 'none':
    print(f"\n{'='*80}")
    print("Regenerating Organized Archive")
    print(f"{'='*80}\n")

    # Create output directories
    for label in TARGET_LABELS + ['misc']:
        (REGENERATED_OUTPUT / label).mkdir(parents=True, exist_ok=True)

    # Create manifests directory
    manifests_dir = REGENERATED_OUTPUT / '_manifests'
    manifests_dir.mkdir(exist_ok=True)

    # Copy files to organized structure
    manifests = {label: [] for label in TARGET_LABELS + ['misc']}
    copy_count = 0

    for result in tqdm(results_list, desc="Organizing files"):
        src_file = ARCHIVE_PATH / result['file']
        final_label = result['final_label']

        if not src_file.exists():
            continue

        # Destination
        dst_file = REGENERATED_OUTPUT / final_label / src_file.name

        # Handle name collisions
        if dst_file.exists():
            stem = dst_file.stem
            suffix = dst_file.suffix
            counter = 1
            while dst_file.exists():
                dst_file = REGENERATED_OUTPUT / final_label / f"{stem}_{counter}{suffix}"
                counter += 1

        # Copy or symlink
        try:
            if COPY_MODE == 'copy':
                shutil.copy2(src_file, dst_file)
            elif COPY_MODE == 'symlink':
                dst_file.symlink_to(src_file.absolute())

            manifests[final_label].append({
                'original': str(result['file']),
                'confidence': result['confidence'],
                'pred_label': result['pred_label']
            })
            copy_count += 1

        except Exception as e:
            print(f"Error copying {src_file}: {e}")

    # Save manifests
    for label, items in manifests.items():
        if items:
            manifest_path = manifests_dir / f"{label}.json"
            with open(manifest_path, 'w') as f:
                json.dump(items, f, indent=2)

    print(f"\n‚úÖ Archive regeneration complete")
    print(f"   Files organized: {copy_count:,}")
    print(f"   Output: {REGENERATED_OUTPUT}")

    # Print distribution
    print(f"\n   Distribution:")
    for label in TARGET_LABELS + ['misc']:
        count = len(manifests[label])
        if count > 0:
            print(f"     {label:10s}: {count:5,} files")

elif COPY_MODE == 'none':
    print("‚ö†Ô∏è  COPY_MODE='none' - skipping materialization")
else:
    print("‚ö†Ô∏è  No results to regenerate")
